  found_text = false
  timer = 0
  timer_max = 10
  while (timer < timer_max) do
#    puts page.body
    found_text = page.evaluate_script("$('input[name=\"schoolId\"]').val() == #{text}")
    break if found_text
    sleep(0.5)
    timer+=1
  end
  found_text.should be_true
end
